# Add on the same level as `required_providers`
# and then run `terraform init -upgrade`
cloud {
  organization = "prefect"
  workspaces {
    name = "<tfc workspace name here>"
  }
}

terraform {
  required_providers {
    prefect = {
      source = "registry.terraform.io/prefecthq/prefect"
    }
  }
}

# For a local instance
provider "prefect" {
  endpoint = "http://localhost:4200"
}

# For a cloud instance
provider "prefect" {
  endpoint = "https://api.stg.prefect.dev"

  # stg: mitch-testing
  # https://app.stg.prefect.dev/account/9a67b081-4f14-4035-b000-1f715f46231b/workspace/85bad5d0-48ee-42be-9f72-8a2998512d85/dashboard
  account_id   = "9a67b081-4f14-4035-b000-1f715f46231b"
  workspace_id = "85bad5d0-48ee-42be-9f72-8a2998512d85"

  api_key = "<key>"
}

resource "prefect_block" "example" {
  # ...
}
